Account.SheetsNewController=Ember.ObjectController.extend({actions:{addOrderLine:function(){this.get("lines").addObject(Account.OrderLine.create())},deleteOrderLine:function(e){this.get("lines").removeObject(e)},addSheet:function(){var e=this.get("model");e.set("createdAt",new Date),Account.Sheet.sheets.addObject(e),this.transitionToRoute("sheets")}}});